(function (w, fn) {
	if (w.addEventListener) w.addEventListener("DOMContentLoaded", fn, false);
	else if (w.attachEvent) w.attachEvent("onload", fn);
})(window, function() {

	function onClick() {
		var	centre = map.getCenter(),
			ll = centre.lat() + "," + centre.lng(),
			url = "http://maps.google.com.au/maps?f=q&source=s_q&hl=en&ie=UTF8&q=Nirvana+Estate&ll=" + ll + "&z=13&iwloc=A&t=" + google.maps.MapTypeId.ROADMAP,
			winsize = "width=" + (screen.availWidth - 10) + ",height=" + (screen.availHeight - 10);

		window.open(url, "_blank", winsize + ",left=0,top=0,status=no,scrollbars=no,resizable=yes,menubar=yes,toolbar=no,location=no");
	};

	var map = new google.maps.Map(document.getElementById("gmap"), {
			mapTypeId: google.maps.MapTypeId.ROADMAP,
			mapTypeControl: true,
			scaleControl: false,
			scrollwheel: false,
			streetViewControl: false,
			navigationControlOptions: { style: google.maps.NavigationControlStyle.SMALL },

			center: new google.maps.LatLng(-26.902733,152
152.612724),

			zoom: 13
		}), clickTgt = map;

	var marker = new google.maps.Marker({
		map: map,
		title: "Click for larger map of Nirvana Estate",
		position: new google.maps.LatLng(-26.902733,152
152.612724)
	});
	clickTgt = marker;


	google.maps.event.addListener(clickTgt, "click", onClick);



});

